(8081, 1, 'pjOption', 5, 'o_ticket_template', '<h2 style=\"text-align: center;\">COMPANY NAME and LOGO</h2>\r\n<h2>BUS TICKET</h2>\r\n<h3>Booking ID: <span class=\"fs10\">{UniqueID}</span></h3>\r\n<table style=\"height: 122px; width: 436px;\" border=\"2\" width=\"436\">\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\">\r\n<h3><strong>Trip Details</strong></h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Bus:</td>\r\n<td><span class=\"fs10\">{Bus}</span></td>\r\n</tr>\r\n<tr>\r\n<td><strong>Date:</strong></td>\r\n<td><strong><span class=\"fs10\"><span class=\"fs10\">{Date}</span></span></strong></td>\r\n</tr>\r\n<tr>\r\n<td><strong>Departure from:</strong></td>\r\n<td><strong><span class=\"fs10\">{From_Location}</span> at <span class=\"fs10\">{Departure_Time}</span></strong></td>\r\n</tr>\r\n<tr>\r\n<td><strong>Arrive to:</strong></td>\r\n<td><strong><span class=\"fs10\">{To_Location}</span> at <span class=\"fs10\">{Arrival_Time}</span></strong></td>\r\n</tr>\r\n<tr>\r\n<td>Ticket Type:</td>\r\n<td><span class=\"fs10\">{TicketType}</span></td>\r\n</tr>\r\n<tr>\r\n<td>Seats:</td>\r\n<td>{Seat}</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<table style=\"height: 122px; width: 436px;\" border=\"2\" width=\"436\">\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\">\r\n<h3><strong>Customer and Booking Details<br /></strong></h3>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Customer Name:</td>\r\n<td><strong><span class=\"fs10\"><span class=\"fs10\">{Title}</span> <span class=\"fs10\">{FirstName}</span> <span class=\"fs10\">{LastName}</span></span></strong></td>\r\n</tr>\r\n<tr>\r\n<td>Phone:</td>\r\n<td><span class=\"fs10\"><span class=\"fs10\"><span class=\"fs10\">{Phone}</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td>Booking Total:</td>\r\n<td><span class=\"fs10\"><span class=\"fs10\">{Total}</span></span></td>\r\n</tr>\r\n<tr>\r\n<td>Online Deposit Payment</td>\r\n<td><span class=\"fs10\">&nbsp;through <span class=\"fs10\">{PaymentMethod}</span><br /></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Notes:</p>\r\n<hr />\r\n<p>{QRCode}</p>\r\n<p>&nbsp;</p>', 'script'),
(8082, 1, 'pjOption', 5, 'o_email_confirmation_subject', 'Booking confirmation', 'data'),
(8083, 1, 'pjOption', 5, 'o_email_confirmation_message', 'You\'ve just made a booking.\r\n\r\nPersonal details:\r\nTitle: {Title}\r\nFirst Name: {FirstName}\r\nLast Name: {LastName}\r\nE-Mail: {Email}\r\nPhone: {Phone}\r\n\r\nBooking details:\r\nBooking date: {Date}\r\nTime: {Time}\r\nBus: {Bus}\r\nRoute: {Route}\r\nSeats: {Seats}\r\nTicket types price: {TicketTypesPrice}\r\nUnique ID: {UniqueID}\r\nTotal: {Total}\r\n\r\nIf you want to print your tickets follow next link: {PrintTickets}\r\n\r\nIf you want to cancel your booking follow next link: {CancelURL}\r\n\r\nThank you, we will contact you ASAP.', 'data'),
(8084, 1, 'pjOption', 5, 'o_email_payment_subject', 'Payment confirmation', 'data'),
